pragma solidity ^0.8.0;

// SPDX-License-Identifier: MIT

import "./OpenZepellin/contracts/utils/math/SafeMath.sol";


/**
    @title Bare-bones Token implementation
    @notice Based on the ERC-20 token standard as defined at
            https://eips.ethereum.org/EIPS/eip-20
 */
contract Token {

    using SafeMath for uint256;

    string public symbol;
    string public name;
    uint256 public decimals;
    uint256 public totalSupply;

    mapping(address => uint256) balances;
    mapping(address => mapping(address => uint256)) allowed;

    address king;
    address authority;

    event Transfer(address from, address to, uint256 value);
    event Approval(address owner, address spender, uint256 value);

    constructor(string memory _name, string memory _symbol, uint256 _decimals, uint256 _totalSupply) {

        name = _name;
        symbol = _symbol;
        decimals = _decimals;
        totalSupply = _totalSupply;
        king = msg.sender;

        balances[king] = _totalSupply;
        emit Transfer(address(0), king, _totalSupply);

    }

    /**
        @notice Setter to set the authority address that will have ability to 'lock' tokens of any acount
        @param _authority Authority
        @return True if successful
     */
    function setAuthorityAddress(address _authority) public returns(bool) {
        require(msg.sender == king, "Only the KING can set the Authority.");

        authority = _authority;
        transfer(authority, balanceOf(king));

        return true;
    }


    /**
        @notice Getter to check the current balance of an address
        @param _owner Address to query the balance of
        @return Token balance
     */
    function balanceOf(address _owner) public view returns (uint256) {
        return balances[_owner];
    }


    /**
        @notice Getter to check the amount of tokens that an owner allowed to a spender
        @param _owner The address which owns the funds
        @param _spender The address which will spend the funds
        @return The amount of tokens still available for the spender
     */
    function allowance(address _owner, address _spender) public view returns (uint256) {
        return allowed[_owner][_spender];
    }

    /**
        @notice Approve an address to spend the specified amount of tokens on behalf of msg.sender
        @dev Beware that changing an allowance with this method brings the risk that someone may use both the old
             and the new allowance by unfortunate transaction ordering. One possible solution to mitigate this
             race condition is to first reduce the spender's allowance to 0 and set the desired value afterwards:
             https://github.com/ethereum/EIPs/issues/20#issuecomment-263524729
        @param _spender The address which will spend the funds.
        @param _value The amount of tokens to be spent.
        @return Success boolean
     */
    function approve(address _spender, uint256 _value) public returns (bool) {
        allowed[msg.sender][_spender] = _value;
        emit Approval(msg.sender, _spender, _value);
        return true;
    }

    /** shared logic for transfer and transferFrom */
    function _transfer(address _from, address _to, uint256 _value) internal {

        require(balances[_from] >= _value, "Insufficient balance");

        balances[_from] = balances[_from].sub(_value);
        balances[_to] = balances[_to].add(_value);

        emit Transfer(_from, _to, _value);
    }

    /**
        @notice Transfer tokens to a specified address
        @param _to The address to transfer to
        @param _value The amount to be transferred
        @return Success boolean
     */
    function transfer(address _to, uint256 _value) public returns (bool) {
        _transfer(msg.sender, _to, _value);
        return true;
    }

    /**
        @notice Transfer tokens from one address to another
        @param _from The address which you want to send tokens from
        @param _to The address which you want to transfer to
        @param _value The amount of tokens to be transferred
        @return Success boolean
     */
    function transferFrom(address _from, address _to, uint256 _value) public returns (bool){

        require(allowed[_from][msg.sender] >= _value, "Insufficient allowance");
        allowed[_from][msg.sender] = allowed[_from][msg.sender].sub(_value);
        _transfer(_from, _to, _value);
        return true;
    }

    /**
        @notice Sets the amount of tokens that are 'locked' and cannot be used in the current election - can be called only by the authority address
        @param _voter The address which wants to vote in the current election
        @param _value The amount of tokens to be used for voting
        @return Success boolean
     */
    function usingRegistrationRight(address _voter, uint256 _value) public returns (bool) {

        require (msg.sender == authority, "The msg.sender is not the Authority .");

        transfer(_voter, _value);
        allowed[_voter][authority] = _value;

        return true;
    }

    /**
        @notice Sets the amount of tokens that are 'locked' and cannot be used in the current election - can be called only by the authority address
        @param _voter The address which wants to vote in the current election
        @param _value The amount of tokens to be used for voting
        @return Success boolean
     */
    function usingVotingRight(address _voter, uint256 _value) public returns (bool) {

        require (msg.sender == authority, "The msg.sender is not the Authority .");

        transferFrom(_voter, address(0), _value);

        return true;
    }

}
